mapper.select:
select b.ID, b.TASK_ID, b.BUSINESS_KEY, b.EXECUTION_ID, b.PROC_INST_ID, b.PROC_DEF_ID, b.NAME, b.PARENT_TASK_ID, b.DESCRIPTION, b.TASK_DEF_KEY, b.OWNER, b.ASSIGNEE, b.CANDIDATE, b.DELEGATION, b.PRIORITY, b.CREATE_TIME, b.END_TIME, b.DUE_DATE, b.CATEGORY, b.TASK_STATE, b.TENANT_ID, b.APP_ID, b.MODULE_ID, b.MODULE_NAME, b.DELETE_REASON, b.FORM_KEY, b.REVISION, b.REMARK1, b.REMARK2, b.REMARK3, b.REMARK4, b.REMARK5, b.REMARK6, b.REMARK7, b.REMARK8, b.REMARK9, b.REMARK10 from ${prefix}WF_UNITE_RUN_TASK b where ((b.category = 'users' and b.candidate = #{userId} and (b.assignee IS NULL or b.ASSIGNEE='')) or (b.assignee = #{userId})
or (b.category = 'groups' and b.assignee in
#{group.id}
) or (b.category = 'groups' and (b.assignee IS NULL or b.ASSIGNEE='') and b.candidate in
#{group.id}
) or (b.category = 'groups' and b.assignee in
'${group.type}:${group.id}'
) or (b.category = 'groups' and (b.assignee IS NULL or b.ASSIGNEE='') and b.candidate in
'${group.type}:${group.id}'
)
) and b.TASK_STATE=#{taskType}
and b.id = #{id}
and b.task_id like '%${taskid}%'
and b.business_key like '%${businesskey}%'
and b.execution_id like '%${executionid}%'
and b.proc_inst_id like '%${procinstid}%'
and b.proc_def_id like '%${procdefid}%'
and b.name like '%${name}%'
and b.parent_task_id like '%${parenttaskid}%'
and b.description like '%${description}%'
and b.task_def_key like '%${taskdefkey}%'
and b.owner like '%${owner}%'
and b.assignee like '%${assignee}%'
and b.candidate like '%${candidate}%'
and b.delegation like '%${delegation}%'
and b.priority like '%${priority}%'
and b.create_time >= #{createtimeb}
and b.create_time <= #{createtimee}
and b.end_time >= #{endtimeb}
and b.end_time <= #{endtimee}
and b.due_date >= #{duedateb}
and b.due_date <= #{duedatee}
and b.category like '%${category}%'
and b.task_state like '%${taskstate}%'
and b.tenant_id = #{tenantId}
and b.tenant_id like #{tenantIdLike}
and (b.tenant_id = '' or b.tenant_id is null)
and b.app_id like '%${appid}%'
and b.module_id like '%${moduleid}%'
and b.module_name like '%${modulename}%'
and b.delete_reason like '%${deletereason}%'
and b.form_key like '%${formkey}%'
and b.revision = #{revision}
and b.remark1 like '%${remark1}%'
and b.remark2 like '%${remark2}%'
and b.remark3 like '%${remark3}%'
and b.remark4 like '%${remark4}%'
and b.remark5 like '%${remark5}%'
and b.remark6 like '%${remark6}%'
and b.remark7 like '%${remark7}%'
and b.remark8 like '%${remark8}%'
and b.remark9 like '%${remark9}%'
and b.remark10 like '%${remark10}%'
order by b.create_time desc

