mapper.select:
select 

	from wf_demo_ask_leave 
	where wf_demo_ask_leave.id in (
		select distinct id from wf_demo_ask_leave inner join

		(select 
			distinct id
			from wf_demo_ask_leave 

	        and wf_demo_ask_leave.id = #{entity.id}
	        and wf_demo_ask_leave.ask_user_id = #{entity.askUserId}
	        and wf_demo_ask_leave.create_time = #{entity.createTime}
	        and wf_demo_ask_leave.ask_start_time = #{entity.askStartTime}
	        and wf_demo_ask_leave.ask_end_time = #{entity.askEndTime}
	        and wf_demo_ask_leave.ask_type = #{entity.askType}
	        and wf_demo_ask_leave.assign_to = #{entity.assignTo}
	        and wf_demo_ask_leave.ask_days = #{entity.askDays}
	        and wf_demo_ask_leave.ask_reasons = #{entity.askReasons}
	        and wf_demo_ask_leave.status = #{entity.status}

		) ${entity.tableAlias} using (id)

)

select
	  wf_demo_ask_leave.id,
	  wf_demo_ask_leave.ask_user_id,
	  wf_demo_ask_leave.create_time,
	  wf_demo_ask_leave.ask_start_time,
	  wf_demo_ask_leave.ask_end_time,
	  wf_demo_ask_leave.ask_type,
	  wf_demo_ask_leave.assign_to,
	  wf_demo_ask_leave.ask_days,
	  wf_demo_ask_leave.ask_reasons,
	  wf_demo_ask_leave.status,
	  u1.user_realname as ask_user_realname,
	  u2.user_realname as assign_to_realname
    from wf_demo_ask_leave,td_sm_user u1,td_sm_user u2 where u1.user_id=wf_demo_ask_leave.ask_user_id and u2.user_id=wf_demo_ask_leave.assign_to
    and wf_demo_ask_leave.id = #{id}
    and wf_demo_ask_leave.ask_user_id = #{askUserId}
    and wf_demo_ask_leave.create_time = #{createTime}
    and wf_demo_ask_leave.ask_start_time = #{askStartTime}
    and wf_demo_ask_leave.ask_end_time = #{askEndTime}
    and wf_demo_ask_leave.ask_type = #{askType}
    and wf_demo_ask_leave.ask_days = #{askDays}
    and wf_demo_ask_leave.ask_reasons = #{askReasons}
    and wf_demo_ask_leave.status = #{status}
